import React, { Component } from "react";
import "./GoogleMap.css";

/* global google */

class GoogleMap extends Component {
    constructor(props) {
        super(props);
        this.state = {
            map: {}
        }
    }

    componentDidMount = () => {
        this.state.map = new google.maps.Map(document.getElementsByClassName('map'), {
            center: { lat: -34.397, lng: 150.644 },
            zoom: 8
        });
    }

    render() {
        return (
            <div className="map">

            </div>
        );
    }
}

export default GoogleMap;
